// src/pages/CourseCrud.js

import React, { useState } from 'react';
import { Container, Form, FormGroup, Label, Input, Button } from 'reactstrap';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import Toast from '../components/Toast'; // Import the Toast component
import useToast from '../hooks/useToast'; // Import the useToast hook

const CourseCrud = () => {
  const [courseName, setCourseName] = useState('');
  const [courseAuthor, setCourseAuthor] = useState('');
  const [url, setUrl] = useState('');
  const location = useLocation();
  const role = location.state?.role || 'user'; // Retrieve the role from the location state
  const { toast, showToast } = useToast(); // Use the useToast hook

  const addCourse = async () => {
    try {
      await axios.post('http://localhost:8080/course/addcourse', {
        coursename: courseName,
        courseauthor: courseAuthor,
        url: url,
      }, {
        params: { role } // Include the role parameter in the request
      });
      setCourseName('');
      setCourseAuthor('');
      setUrl('');
      showToast('Course added to the database'); // Show toast message
    } catch (error) {
      console.error('Error adding course:', error);
    }
  };

  return (
    <Container className="my-5">
      <Toast message={toast.message} show={toast.show} /> {/* Add Toast component */}
      <h2 className="text-center mb-4">Courses</h2>
      <Form onSubmit={(e) => e.preventDefault()}>
        <FormGroup>
          <Label for="courseName">Course Name</Label>
          <Input
            type="text"
            name="courseName"
            id="courseName"
            value={courseName}
            onChange={(e) => setCourseName(e.target.value)}
          />
          <Label for="courseAuthor">Course Author</Label>
          <Input
            type="text"
            name="courseAuthor"
            id="courseAuthor"
            value={courseAuthor}
            onChange={(e) => setCourseAuthor(e.target.value)}
          />
          <Label for="url">URL</Label>
          <Input
            type="text"
            name="url"
            id="url"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
        </FormGroup>
        <Button color="primary" onClick={addCourse}>
          Add Course
        </Button>
      </Form>
    </Container>
  );
};

export default CourseCrud;
