import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, CardBody, CardSubtitle, CardText, Button, Container } from 'reactstrap';
import { useLocation, useNavigate } from 'react-router-dom';

const ManageCourses = () => {
    const [videos, setVideos] = useState([]);
    const [courses, setCourses] = useState([]);
    const [newCourse, setNewCourse] = useState({ coursename: '', courseauthor: '', url: '' });
    const location = useLocation();
    const role = location.state?.role || 'user'; // Keep this line to get the role from location state
    const navigate = useNavigate(); // Initialize navigate


    useEffect(() => {
        const fetchVideos = async () => {
            try {
                const response = await axios.get('http://localhost:8080/course/getall');
                if (Array.isArray(response.data)) {
                    setVideos(response.data);
                } else {
                    console.error('Expected an array but got:', response.data);
                }
            } catch (error) {
                console.error('Error fetching videos:', error);
            }
        };

        fetchVideos();

        // fetchCourses();
    }, []);

    const fetchCourses = async () => {
        try {
            const response = await axios.get('http://localhost:8080/course/getall');
            setCourses(response.data);
        } catch (error) {
            console.error('Error fetching courses:', error);
        }
    };

    const handleAddCourse = async () => {
        try {
            await axios.post('http://localhost:8080/course/addcourse', newCourse, {
                params: { role }
            });
            fetchCourses();
            setNewCourse({ coursename: '', courseauthor: '', url: '' });
        } catch (error) {
            alert('Error adding course: ' + error.message);
        }
    };

    const handleDeleteCourse = async (courseId) => {
        try {
            await axios.delete('http://localhost:8080/course/deletecourse', {
                params: { courseId, role }
            });
            fetchCourses();
        } catch (error) {
            alert('Error deleting course: ' + error.message);
        }
    };

    return (
        <div style={{ backgroundColor: "silver" }}>
            <h1 className="text-danger text-center">
                List of available Courses
            </h1>
            {role === 'admin' && (
                <div className="d-flex justify-content-center mt-3">
                    <Button color="primary" onClick={() => navigate('/addcourses', { state: { role: 'admin' } })}>
                        Add Course
                    </Button>
                </div>
            )}
            <div className="d-flex flex-wrap justify-content-center align-items-center">
                {videos.map((video, index) => (
                    <Card key={index} className="m-3" style={{ width: '400px' }}>
                        <CardBody className="p-3">
                            <CardSubtitle tag="h5" className="font-weight-bold">{video.coursename}</CardSubtitle>
                            <CardText>Course Author: {video.courseauthor}</CardText>
                            <Container className="embed-responsive embed-responsive-16by9">
                                <div className="embed-responsive-item">
                                    <iframe
                                        title={`Video ${index}`}
                                        className="w-100"
                                        src={`https://www.youtube.com/embed/${extractVideoId(video.url)}`}
                                        allowFullScreen
                                    ></iframe>
                                </div>
                            </Container>
                            <Container className="d-flex justify-content-center">
                                <Button color="danger" href={video.url} target="_blank" rel="noopener noreferrer">
                                    View On Youtube
                                </Button>
                                {role === 'admin' && (
                                    <Button color="danger" onClick={() => handleDeleteCourse(video.id)}>Delete</Button>
                                )}
                            </Container>
                        </CardBody>
                    </Card>
                ))}
            </div>
        </div>
    );
};

const extractVideoId = (url) => {
    const videoId = url.split('v=')[1];
    if (videoId) {
        const ampersandPosition = videoId.indexOf('&');
        if (ampersandPosition !== -1) {
            return videoId.substring(0, ampersandPosition);
        }
        return videoId;
    }
    return null;
};

export default ManageCourses;
