import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import '../App.css';
import { Button } from 'reactstrap';

const LogIn = () => {
  const [password, setPassword] = useState('');
  const [userid, setUserId] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [failedMessage, setFailedMessage] = useState('');

  const navigate = useNavigate();

  const handleUserIdChange = (e) => {
    setUserId(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/demo/logIn', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userid, password }),
      });

      if (response.status === 200) {
        setSuccessMessage('Login successful');
        const data = await response.json(); // Parse response body as JSON

        if (data.role === 'admin') {
          navigate('/managecourses', { state: { role: 'admin' } });
        } else {
          navigate('/managecourses', { state: { role: 'user' } });
        }
      } else {
        setFailedMessage('Incorrect userid or password');
        throw new Error('Authentication failed');
      }
    } catch (error) {
      if (error.message !== 'Authentication failed') {
        setFailedMessage('Something went wrong... try again!');
      }
      console.error('Error:', error);
    }
  };

  return (
    <div className="login-container">
      <h2>Login to Your Account</h2>
      <form onSubmit={handleSubmit}>
        <br />
        <label>
          Userid: <br />
          <input type="text" value={userid} onChange={handleUserIdChange} required />
        </label>
        <br />
        <br />
        <label>
          Password: <br />
          <input type="password" value={password} onChange={handlePasswordChange} required />
        </label>
        <br />
        <br />
        <div className="login-container-btn">
          <Button type="submit" color='primary'>Login</Button>
          <Button type="button" color='warning ml-3' onClick={() => navigate("/signup")}>Go to Signup</Button>
        </div>
        <br />
        <br />
        {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
        {failedMessage && <p style={{ color: 'red' }}>{failedMessage}</p>}
      </form>
    </div>
  );
};

export default LogIn;
