import React, { useState } from 'react';
import '.components//CourseContentView.css'; // Make sure to create this CSS file

const CourseContentView = ({ contents }) => {
    const [selectedContent, setSelectedContent] = useState(contents[0]);

    return (
        <div className="course-content-view">
            <div className="content-list">
                <h3>Course Content</h3>
                <ul>
                    {contents.map((content, index) => (
                        <li 
                            key={index} 
                            className={content.name === selectedContent.name ? 'active' : ''}
                            onClick={() => setSelectedContent(content)}
                        >
                            {content.name}
                        </li>
                    ))}
                </ul>
            </div>
            <div className="content-details">
                <h3>{selectedContent.name}</h3>
                <div className="video-container">
                    <iframe
                        title={selectedContent.name}
                        src={`https://www.youtube.com/embed/${extractVideoId(selectedContent.videoUrl)}`}
                        allowFullScreen
                    ></iframe>
                </div>
                <div className="transcript">
                    <h4>Video Transcript</h4>
                    <p>{selectedContent.transcript}</p>
                </div>
            </div>
        </div>
    );
};

const extractVideoId = (url) => {
    if (!url) return '';
    const videoId = url.split('v=')[1];
    if (videoId) {
        const ampersandPosition = videoId.indexOf('&');
        if (ampersandPosition !== -1) {
            return videoId.substring(0, ampersandPosition);
        }
        return videoId;
    }
    return '';
};

export default CourseContentView;
