import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
// import '../App.css';
import '../components/css/LogIn.css'; // Import the CSS file
import { Button } from 'reactstrap';

const LogIn = () => {
  const [password, setPassword] = useState('');
  const [userid, setUserId] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [failedMessage, setFailedMessage] = useState('');

  const navigate = useNavigate();

  const handleUserIdChange = (e) => {
    setUserId(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/demo/logIn', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userid, password }),
      });

      if (response.status === 200) {
        setSuccessMessage('Login successful');
        const data = await response.json(); // Parse response body as JSON

        if (data.role === 'admin') {
          navigate('/managecourses', { state: { role: 'admin' } });
        } else {
          navigate('/managecourses', { state: { role: 'user' } });
        }
      } else {
        setFailedMessage('Incorrect userid or password');
        throw new Error('Authentication failed');
      }
    } catch (error) {
      if (error.message !== 'Authentication failed') {
        setFailedMessage('Something went wrong... try again!');
      }
      console.error('Error:', error);
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Login</h2>
        <label htmlFor="userid">User id</label>
        <input
          type="text"
          id="userid"
          value={userid}
          onChange={handleUserIdChange}
          required
        />
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
          required
        />
        <div className="login-buttons">
          <Button type="submit" color="primary">Login</Button>
          <Button type="button" color="warning" onClick={() => navigate("/signup")}>Go to Signup</Button>
        </div>
        {successMessage && <p className="login-message" style={{ color: 'green' }}>{successMessage}</p>}
        {failedMessage && <p className="login-message" style={{ color: 'red' }}>{failedMessage}</p>}
      </form>
    </div>
  );
};

export default LogIn;
