import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Button, Form, FormGroup, Label, Input, Table } from 'reactstrap';

const ManageCourses = () => {
    const [courses, setCourses] = useState([]);
    const history = useHistory();

    useEffect(() => {
        fetchCourses();
    }, []);

    const fetchCourses = async () => {
        try {
            const response = await axios.get('http://localhost:8080/course/getall');
            setCourses(response.data);
        } catch (error) {
            console.error('Error fetching courses:', error);
        }
    };

    const handleDeleteCourse = async (courseId) => {
        if (window.confirm('Are you sure you want to delete this course?')) {
            try {
                await axios.delete('http://localhost:8080/course/deletecourse', {
                    params: { courseId, role: 'admin' }
                });
                fetchCourses();
            } catch (error) {
                alert('Error deleting course: ' + error.message);
            }
        }
    };

    const handleAddCourse = () => {
        history.push('/addcourse');
    };

    return (
        <Container>
            <h2>Manage Courses</h2>

            <Button color="primary" onClick={handleAddCourse} className="mb-3">Add Course</Button>

            <div className="row">
                {courses.map((course) => (
                    <div key={course.id} className="col-md-4 mb-4">
                        <Card>
                            <CardBody>
                                <CardTitle tag="h5">{course.coursename}</CardTitle>
                                <CardText>{course.courseauthor}</CardText>
                                <CardText>{course.url}</CardText>
                                <Button color="danger" onClick={() => handleDeleteCourse(course.id)}>Delete</Button>
                            </CardBody>
                        </Card>
                    </div>
                ))}
            </div>
        </Container>
    );
};

export default ManageCourses;
