import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Card, CardBody, CardSubtitle, CardText, Button, Container } from 'reactstrap';
import { useLocation, useNavigate } from 'react-router-dom';
import { confirmAlert } from 'react-confirm-alert'; // Import the confirm alert
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import the CSS for react-confirm-alert
import '../components/css/Courses.css'; // Import the CSS file
import { div } from 'three/examples/jsm/nodes/Nodes.js';
import * as THREE from 'three';
import GLOBE from 'vanta/dist/vanta.globe.min';

const TypingAnimation = () => {
    const [text, setText] = useState('');
    const words = ["Hello", "Welcome", "to", "the", "typing", "animation!"];
    const [wordIndex, setWordIndex] = useState(0);
    const [letterIndex, setLetterIndex] = useState(0);
    const [direction, setDirection] = useState(1); // 1 for typing, -1 for erasing
    const typingSpeed = 75; // milliseconds per character
  
    useEffect(() => {
      const intervalId = setInterval(() => {
        const word = words[wordIndex];
        const currentText = word.slice(0, letterIndex);
        setText(currentText);
        
        if (direction === 1) {
          if (letterIndex < word.length) {
            setLetterIndex(letterIndex + 1);
          } else {
            setDirection(-1);
            setTimeout(() => setDirection(1), 1000); // Pause before erasing
          }
        } else {
          if (letterIndex > 0) {
            setLetterIndex(letterIndex - 1);
          } else {
            setDirection(1);
            setWordIndex((wordIndex + 1) % words.length); // Move to the next word
          }
        }
      }, typingSpeed);
  
      return () => clearInterval(intervalId);
    }, [letterIndex, direction, wordIndex]);
  
    return (
      <h1>{text}<span className="cursor" /></h1>
    );
  };

const ManageCourses = () => {
    const [videos, setVideos] = useState([]);
    const [courses, setCourses] = useState([]);
    const [newCourse, setNewCourse] = useState({ coursename: '', courseauthor: '', url: '' });
    const location = useLocation();
    const role = location.state?.role || 'user';
    const vantaRef = useRef(null);
    const navigate = useNavigate();

    useEffect(() => {
        const vantaEffect = GLOBE({
            el: vantaRef.current,
            mouseControls: true,
            touchControls: true,
            gyroControls: false,
            minHeight: 200.00,
            minWidth: 200.00,
            scale: 1.00,
            scaleMobile: 1.00,
            color: 0x3fffcf,
            backgroundColor: 0x20018,
            THREE,
        });

        return () => {
            if (vantaEffect) vantaEffect.destroy();
        };
    }, []);

    useEffect(() => {
        const fetchVideos = async () => {
            try {
                const response = await axios.get('http://localhost:8080/course/getall');
                if (Array.isArray(response.data)) {
                    setVideos(response.data);
                } else {
                    console.error('Expected an array but got:', response.data);
                }
            } catch (error) {
                console.error('Error fetching videos:', error);
            }
        };

        fetchVideos();
    }, []);

    const fetchCourses = async () => {
        try {
            const response = await axios.get('http://localhost:8080/course/getall');
            setCourses(response.data);
        } catch (error) {
            console.error('Error fetching courses:', error);
        }
    };

    const handleAddCourse = async () => {
        try {
            await axios.post('http://localhost:8080/course/addcourse', newCourse, {
                params: { role }
            });
            fetchCourses();
            setNewCourse({ coursename: '', courseauthor: '', url: '' });
        } catch (error) {
            alert('Error adding course: ' + error.message);
        }
    };

    const handleDeleteCourse = async (courseId) => {
        try {
            await axios.delete('http://localhost:8080/course/deletecourse', {
                params: { courseId, role }
            });
            fetchCourses();
        } catch (error) {
            alert('Error deleting course: ' + error.message);
        }
    };

    const confirmDelete = (courseId) => {
        confirmAlert({
            title: 'Confirm to delete',
            message: 'Are you sure you want to delete this course?',
            buttons: [
                {
                    label: 'Yes',
                    onClick: () => handleDeleteCourse(courseId)
                },
                {
                    label: 'No',
                    onClick: () => { }
                }
            ],
            closeOnEscape: true,
            closeOnClickOutside: true
        });
    };

    return (
        <div>
            {role === 'admin' ? (
                <h1 className="header" style={{color: "#000000"}}>
                    Admin Dashboard
                </h1>
            ) : (
                <div ref={vantaRef} className="userdash">
                    <h1 className="header" style={{color: "#ffffff"}}>
                        List of available Courses for you
                    </h1>

                    <h1>add typing animation</h1>

                </div>
            )}
            {role === 'admin' && (
                <div className="button-section">
                    <Button className="add-course-button" onClick={() => navigate('/addcourse', { state: { role: 'admin' } })}>
                        Add New Course
                    </Button>
                </div>
            )}
            <div className="video-cards">
                {videos.map((video, index) => (
                    <Card key={index} className="video-card">
                        <CardBody className="video-card-body">
                            <CardSubtitle tag="h5" className="video-card-title">{video.coursename}</CardSubtitle>
                            <CardText className="video-card-text">{video.courseauthor}</CardText>
                            <Container className="video-container">
                                <iframe
                                    title={`Video ${index}`}
                                    className="embed-responsive-item"
                                    src={`https://www.youtube.com/embed/${extractVideoId(video.url)}`}
                                    allowFullScreen
                                ></iframe>
                            </Container>
                            <Container className="button-group">
                                <Button className="view-btn" href={video.url} target="_blank" rel="noopener noreferrer">
                                    View On Youtube
                                </Button>
                                {role === 'admin' && (
                                    <Button className="delete-btn" onClick={() => confirmDelete(video.id)}>Delete</Button>
                                )}
                            </Container>
                        </CardBody>
                    </Card>
                ))}
            </div>
        </div>
    );
};



const extractVideoId = (url) => {
    const videoId = url.split('v=')[1];
    if (videoId) {
        const ampersandPosition = videoId.indexOf('&');
        if (ampersandPosition !== -1) {
            return videoId.substring(0, ampersandPosition);
        }
        return videoId;
    }
    return null;
};

export default ManageCourses;
