import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Button, Form, FormGroup, Label, Input, Table } from 'reactstrap';
import { useLocation } from 'react-router-dom';

const ManageCourses = () => {
    const [videos, setVideos] = useState([]);
    const [courses, setCourses] = useState([]);
    const [newCourse, setNewCourse] = useState({ coursename: '', courseauthor: '', url: '' });
    const location = useLocation();
    const role = location.state?.role || 'user';

    useEffect(() => {
        const fetchVideos = async () => {
            try {
              const response = await axios.get('http://localhost:8080/course/getall');
              if (Array.isArray(response.data)) {
                setVideos(response.data);
              } else {
                console.error('Expected an array but got:', response.data);
              }
            } catch (error) {
              console.error('Error fetching videos:', error);
            }
          };
      
          fetchVideos();
        fetchCourses();
    }, []);

    const fetchCourses = async () => {
        try {
            const response = await axios.get('http://localhost:8080/course/getall');
            setCourses(response.data);
        } catch (error) {
            console.error('Error fetching courses:', error);
        }
    };

    const handleAddCourse = async () => {
        try {
            await axios.post('http://localhost:8080/course/addcourse', newCourse, {
                params: { role }
            });
            fetchCourses();
            setNewCourse({ coursename: '', courseauthor: '', url: '' });
        } catch (error) {
            alert('Error adding course: ' + error.message);
        }
    };

    const handleDeleteCourse = async (courseId) => {
        try {
            await axios.delete('http://localhost:8080/course/deletecourse', {
                params: { courseId, role }
            });
            fetchCourses();
        } catch (error) {
            alert('Error deleting course: ' + error.message);
        }
    };

    return (
        <Container>
            <h2>Manage Courses</h2>
            {role === 'admin' && (
                <Form inline className="mb-4">
                    <FormGroup className="mr-2">
                        <Label for="coursename" className="mr-2">Course Name</Label>
                        <Input
                            type="text"
                            name="coursename"
                            id="coursename"
                            value={newCourse.coursename}
                            onChange={(e) => setNewCourse({ ...newCourse, coursename: e.target.value })}
                        />
                    </FormGroup>
                    <FormGroup className="mr-2">
                        <Label for="courseauthor" className="mr-2">Course Author</Label>
                        <Input
                            type="text"
                            name="courseauthor"
                            id="courseauthor"
                            value={newCourse.courseauthor}
                            onChange={(e) => setNewCourse({ ...newCourse, courseauthor: e.target.value })}
                        />
                    </FormGroup>
                    <FormGroup className="mr-2">
                        <Label for="url" className="mr-2">URL</Label>
                        <Input
                            type="text"
                            name="url"
                            id="url"
                            value={newCourse.url}
                            onChange={(e) => setNewCourse({ ...newCourse, url: e.target.value })}
                        />
                    </FormGroup>
                    <Button color="primary" onClick={handleAddCourse}>Add Course</Button>
                </Form>
            )}

            <Table>
                <thead>
                    <tr>
                        <th>Course Name</th>
                        <th>Course Author</th>
                        <th>URL</th>
                        {role === 'admin' && <th>Actions</th>}
                    </tr>
                </thead>
                <tbody>
                    {courses.map((course) => (
                        <tr key={course.id}>
                            <td>{course.coursename}</td>
                            <td>{course.courseauthor}</td>
                            <td>{course.url}</td>
                            {role === 'admin' && (
                                <td>
                                    <Button color="danger" onClick={() => handleDeleteCourse(course.id)}>Delete</Button>
                                </td>
                            )}
                        </tr>
                    ))}
                </tbody>
            </Table>
        </Container>
    );
};

export default ManageCourses;
