import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Card, CardBody, CardSubtitle, CardText, Button, Container } from 'reactstrap';
import { useLocation, useNavigate } from 'react-router-dom';
import { confirmAlert } from 'react-confirm-alert'; // Import the confirm alert
import 'react-confirm-alert/src/react-confirm-alert.css'; // Import the CSS for react-confirm-alert
import '../components/css/Courses.css'; // Import the CSS file
import * as THREE from 'three';
import GLOBE from 'vanta/dist/vanta.globe.min';
import Typewriter from 'typewriter-effect';
import WelcomeTypewriter from './WelcomeTypewriter';

const ManageCourses = () => {
    const [videos, setVideos] = useState([]);
    const [courses, setCourses] = useState([]);
    const [newCourse, setNewCourse] = useState({ coursename: '', courseauthor: '', url: '' });
    const location = useLocation();
    const role = location.state?.role || 'user';
    const username = location.state?.username;
    const vantaRef = useRef(null);
    const navigate = useNavigate();

    useEffect(() => {
        if (!vantaRef.current) return;

        const vantaEffect = GLOBE({
            el: vantaRef.current,
            mouseControls: true,
            touchControls: true,
            gyroControls: false,
            minHeight: 100.00,
            minWidth: 100.00,
            scale: 1.00,
            scaleMobile: 1.00,
            color: 0x3fffcf,
            backgroundColor: 0x20018,
            THREE,
        });

        return () => {
            if (vantaEffect) vantaEffect.destroy();
        };
    }, [vantaRef.current]);

    useEffect(() => {
        const fetchVideos = async () => {
            try {
                const response = await axios.get('http://localhost:8080/course/getall');
                if (Array.isArray(response.data)) {
                    setVideos(response.data);
                } else {
                    console.error('Expected an array but got:', response.data);
                }
            } catch (error) {
                console.error('Error fetching videos:', error);
            }
        };

        fetchVideos();
    }, []);

    const fetchCourses = async () => {
        try {
            const response = await axios.get('http://localhost:8080/course/getall');
            setCourses(response.data);
        } catch (error) {
            console.error('Error fetching courses:', error);
        }
    };

    const handleAddCourse = async () => {
        try {
            await axios.post('http://localhost:8080/course/addcourse', newCourse, {
                params: { role }
            });
            fetchCourses();
            setNewCourse({ coursename: '', courseauthor: '', url: '' });
        } catch (error) {
            alert('Error adding course: ' + error.message);
        }
    };

    const handleDeleteCourse = async (courseId) => {
        try {
            await axios.delete('http://localhost:8080/course/deletecourse', {
                params: { courseId, role }
            });
            fetchCourses();
        } catch (error) {
            alert('Error deleting course: ' + error.message);
        }
    };

    const confirmDelete = (courseId) => {
        confirmAlert({
            title: 'Confirm to delete',
            message: 'Are you sure you want to delete this course?',
            buttons: [
                {
                    label: 'Yes',
                    onClick: () => handleDeleteCourse(courseId)
                },
                {
                    label: 'No',
                    onClick: () => { }
                }
            ],
            closeOnEscape: true,
            closeOnClickOutside: true
        });
    };

    return (
        <div>
            <button className="LogOut-Btn" onClick={() => navigate('/', { state: { role: 'admin' } })}>
                <div className="sign">
                    <svg viewBox="0 0 512 512">
                        <path d="M377.9 105.9L500.7 228.7c7.2 7.2 11.3 17.1 11.3 27.3s-4.1 20.1-11.3 27.3L377.9 406.1c-6.4 6.4-15 9.9-24 9.9c-18.7 0-33.9-15.2-33.9-33.9l0-62.1-128 0c-17.7 0-32-14.3-32-32l0-64c0-17.7 14.3-32 32-32l128 0 0-62.1c0-18.7 15.2-33.9 33.9-33.9c9 0 17.6 3.6 24 9.9zM160 96L96 96c-17.7 0-32 14.3-32 32l0 256c0 17.7 14.3 32 32 32l64 0c17.7 0 32 14.3 32 32s-14.3 32-32 32L96 480c-53 0-96-43-96-96L0 128c0-53 43-96 96-96l64 0c17.7 0 32 14.3 32 32S177.7 96 160 96z" />
                    </svg>
                </div>
                <div className="text">Logout</div>
            </button>

            <div ref={vantaRef} style={{ height: '100vh', width: '100%' }}>
                <div className="manage-courses-container">
                    <WelcomeTypewriter username={username} />
                    <Container className="video-grid">
                        {videos.map(video => (
                            <Card key={video.id} className="video-card">
                                <CardBody>
                                    <CardSubtitle tag="h5" className="video-title">{video.coursename}</CardSubtitle>
                                    <CardText className="video-author">{video.courseauthor}</CardText>
                                    <Button onClick={() => navigate(`/course/${video.id}`, { state: { role } })}>View</Button>
                                    {role === 'admin' && (
                                        <Button className="delete-btn" onClick={() => confirmDelete(video.id)}>Delete</Button>
                                    )}
                                </CardBody>
                            </Card>
                        ))}
                    </Container>

                    <Container className="add-course-form">
                        <h2>Add New Course</h2>
                        <input
                            type="text"
                            placeholder="Course Name"
                            value={newCourse.coursename}
                            onChange={e => setNewCourse({ ...newCourse, coursename: e.target.value })}
                        />
                        <input
                            type="text"
                            placeholder="Course Author"
                            value={newCourse.courseauthor}
                            onChange={e => setNewCourse({ ...newCourse, courseauthor: e.target.value })}
                        />
                        <input
                            type="text"
                            placeholder="Course URL"
                            value={newCourse.url}
                            onChange={e => setNewCourse({ ...newCourse, url: e.target.value })}
                        />
                        <Button onClick={handleAddCourse}>Add Course</Button>
                    </Container>
                </div>
            </div>
        </div>
    );
};

export default ManageCourses;
