import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import '../components/css/LogIn.css'; // Import the CSS file
import { Button } from 'reactstrap';
import * as THREE from 'three';
import GLOBE from 'vanta/dist/vanta.globe.min';

const SignUp = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [userid, setUserId] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [failedMessage, setFailedMessage] = useState('');
  const vantaRef = useRef(null);

  const navigate = useNavigate();

  useEffect(() => {
    const vantaEffect = GLOBE({
      el: vantaRef.current,
      mouseControls: true,
      touchControls: true,
      gyroControls: false,
      minHeight: 200.00,
      minWidth: 200.00,
      scale: 1.00,
      scaleMobile: 1.00,
      color: 0x3fffcf,
      backgroundColor: 0x20018,
      THREE,
    });

    return () => {
      if (vantaEffect) vantaEffect.destroy();
    };
  }, []);

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handleUserIdChange = (e) => {
    setUserId(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Send the data to your Spring Boot API
    fetch('http://localhost:8080/demo/signUp', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, userid, password }),
    })
      .then((response) => {
        if (response.status === 200) {
          // Show success message on the form
          setSuccessMessage('User created successfully');
        }
        return response.json();
      })
      .then((data) => {
        // Handle the data from the response
        console.log(data);
      })
      .catch((error) => {
        if (error.status === !200) {
          setFailedMessage('Something went wrong.. try again !')
        }
        console.error('Error:', error);
      });
  };

  return (
    <div ref={vantaRef} className="login-container">
      <div className="welcome">
        <h1>Welcome to StudyNest</h1>
        <p>Discover, Learn, Achieve</p>
      </div>
      <div className="login-form-container">
        <form className="login-form" onSubmit={handleSubmit}>
          <h2>SignUp</h2>
          <label htmlFor="userid">Username</label>
          <input
            type="text"
            id="username"
            value={userid}
            onChange={handleUsernameChange}
            required
          />
          <label htmlFor="userid">User id</label>
          <input
            type="text"
            id="userid"
            value={userid}
            onChange={handleUserIdChange}
            required
          />
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
          <div className="login-buttons">
            <Button type="submit" color="primary">SignUp</Button>
            {/* <Button type="button" color="warning" onClick={() => navigate("/signup")}>Go to Signup</Button> */}
            <a href='/'>Already have an account? SignIn</a>
          </div>
          {successMessage && <p className="login-message" style={{ color: 'green' }}>{successMessage}</p>}
          {failedMessage && <p className="login-message" style={{ color: 'red' }}>{failedMessage}</p>}
        </form>
      </div>
    </div>
  );
};

export default SignUp;
