// TypingAnimation.jsx

import React, { useState, useEffect } from 'react';

const TypingAnimation = ({ words, typingSpeed, pauseAfterType }) => {
  const [text, setText] = useState('');
  const [index, setIndex] = useState(0);

  useEffect(() => {
    let timeout;

    const typeNextLetter = () => {
      if (index < words.length) {
        const word = words[index];
        const newText = word.substring(0, text.length + 1);
        setText(newText);

        if (newText === word) {
          timeout = setTimeout(() => {
            eraseText();
          }, pauseAfterType);
        } else {
          timeout = setTimeout(typeNextLetter, typingSpeed);
        }
      }
    };

    const eraseText = () => {
      if (text.length === 0) {
        setIndex((prevIndex) => (prevIndex === words.length - 1 ? 0 : prevIndex + 1));
      } else {
        const newText = text.substring(0, text.length - 1);
        setText(newText);
        timeout = setTimeout(eraseText, typingSpeed / 2);
      }
    };

    timeout = setTimeout(typeNextLetter, typingSpeed);

    return () => clearTimeout(timeout);
  }, [index, text, words, typingSpeed, pauseAfterType]);

  return <span>{text}</span>;
};

export default TypingAnimation;
